import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import API from '../../utils/API';

import Step, { StepWrapper } from '../Steps/Step';
import styled from 'styled-components';

import { candy } from '../../utils/colors';

const Wrapper = styled('div')`
  text-align: center;
`

const AddButton = styled(Link)`
  color: ${candy};
  &:hover {

  }
`
export default class ShowTutorial extends Component {

  state = {
    tutorial: {},
    steps: []
  }

  componentDidMount() {
    const tutorialId = this.props.match.params.id;
    this.fetchTutorial(tutorialId);
    this.fetchSteps(tutorialId);
  }

  fetchTutorial = (tutorialId) => {
    API.fetchTutorial(tutorialId)
      .then(response => {
        this.setState({ tutorial: response.data });
      })
      .catch(function (error) {
        console.log(error);
      })
  }

  fetchSteps = (tutorialId) => {
    API.fetchSteps(tutorialId)
      .then(response => {
        console.log(response.data);
        this.setState({ steps: response.data })
      })
      .catch(function (error) {
        console.log(error);
      })
  }

  deleteStep = (stepId) => {
    API.deleteStep(stepId)
      .then(response => {
        console.log(response.data)
        this.fetchSteps(this.props.match.params.id);
      })
      .catch(function (error) {
        console.log(error);
      })
  }

  deleteTutorial = (tutorialId) => {
    API.deleteTutorial(tutorialId)
      .then(response => {
        alert(response.data)
        window.location.pathname = '/';
      })
      .catch(function (error) {
        console.log(error);
      })
  }

  renderSteps = () => {
    console.log(this.state.steps);
    return (
      !this.state.steps
        ?
        (this.state.steps.map(({ _id, step_description, step_media }) => {
          return (
            <Step
              key={_id}
              _id={_id}
              step_description={step_description}
              step_media={step_media}
              deleteStep={this.deleteStep}
            />
          )
        }))
        : <p style={{ 'margin-top': '2em' }}>
          There are no steps for this tutorial yet.
        </p>
    )
  }


  renderAnchors() {
    return (
      this.state.steps.map(({ _id }) => {
        return (
          <a href={`/steps/${_id}`} key={_id}>
            <span className="material-icons"> panorama_fish_eye </span>
          </a>
        )
      })
    )
  }

  render() {
    const { tutorial } = this.state
    return (
      <Wrapper>
        <h1>Your tutorial : {tutorial.tutorial_title}</h1>
        <h2>Steps</h2>
        <div>{this.renderAnchors()}</div>
        <div className="steps">
          {this.renderSteps()}
        </div>
        <AddButton
          to={`/tutorials/${tutorial._id}/steps/new`}
          title='Add a step'

        >
          <span class="material-icons">add_circle</span>
        </AddButton>
        <Link to='' onClick={() => this.deleteTutorial(tutorial._id)}>Delete this tutorial</Link>
      </Wrapper>
    )
  }
}
