import React, { Component } from 'react'


class ImageComponent extends Component {
  state = {
    isOpened: false
  }

  toggleModal = () => {
    this.setState({ isOpened: !this.state.isOpened })
  }

  render() {
    return (
      <div>
        <img
          className="small"
          src={this.props.src}
          onClick={this.toggleModal}
          alt={this.props.alt}
          width='500px'
        />
      </div>
    )
  }
}

export default ImageComponent;
