import styled from "styled-components";
import { purple, satin, blizzardBlue, tumbleweed, candy } from "../../utils/colors";

export const Input = styled.input`
  padding: 1em 2em;
  margin: 2em;
  width: 20em;
  background: none;
  border: none;
  font-size: 1.5em;
  font-family: 'Roboto', sans-serif;
  font-weight: 700;
  color: ${tumbleweed};
  &:focus {
    color: ${tumbleweed};
    outline: none;
  }
`

export const Form = styled.form`
  display: flex;
  justify-content: center;
  align-items: center;
`

export const SubmitButton = styled.button`
  height: fit-content;
  background: ${satin};
  border: none;
  color: ${blizzardBlue};
  padding: 1em 2em;
  transition: all 0.3s ease;
  &:hover {
    background: ${candy};
    transform: translate(0, -0.3em);
    cursor: pointer;
  }
`

export const TextArea = styled.textarea`
  padding: 1em  2em;
  margin: 2em;
  height: 15em;
  width: 30em;
  resize: none;
  background: ${tumbleweed};
  font-size: 1.5em;
  border: 1px solid ${tumbleweed};
  font-family: 'Roboto', sans-serif;
  font-weight: 700;
  color: ${tumbleweed};
  overflow: scroll;
  &:focus {
    color: ${tumbleweed};
    outline: none;
  }
`;
