import React, { Component } from 'react'
import styled from 'styled-components'
import { satin } from '../../utils/colors';

export const StepWrapper = styled.section`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  ${'' /* padding: 2em; */}
  ${'' /* border-bottom: 1px solid ${satin}; */}
  border-radius: 2em;
  background-color: ${satin};
`;

const LeftSide = styled.div`
  width: 100%;
  height: 100%;
  text-align: right;
`;

const Icon = styled.span`
  &:hover {
    cursor: pointer;
  }
`

class Step extends Component {
  render() {
    const { _id, step_description, step_media, deleteStep } = this.props
    return (
      <StepWrapper>
        <LeftSide>
          <p style={{ 'textAlign': 'right' }}>{step_description}</p>
        </LeftSide>
        <img
          src={step_media}
          alt={step_description}
          style={{ 'marginLeft': '5em', 'maxWidth': '60%', 'maxHeight': '100%' }}
        />
        <span
          className="material-icons"
          onClick={() => deleteStep(_id)}>
          highlight_off
        </span>
      </StepWrapper>
    )
  }
}

export default Step;
