import React, { Component } from 'react'
import styled from 'styled-components'
import { satin } from '../../utils/colors';

export const StepWrapper = styled.section`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  padding: 2em;
  border-bottom: 1px solid ${satin};
`;

const LeftSide = styled.div`
  text-align: right;
`;

class Step extends Component {
  render() {
    const { _id, step_description, step_media, deleteStep } = this.props
    return (
      <StepWrapper>
        <LeftSide>
          <p style={{ 'text-align': 'right' }}>{step_description}</p>
        </LeftSide>
        <img
          src={step_media}
          alt={step_description}
          width='50%'
          style={{ 'margin-left': '5em' }}
        />
        {/* <p onClick={() => deleteStep(_id)}>Delete this step</p> */}
      </StepWrapper>
    )
  }
}

export default Step;
