import Axios from 'axios';

export default {
  fetchTutorial: function (tutorialId) {
    return Axios.get(`api/tutorials/${tutorialId}`)
  },

  fetchSteps: function (tutorialId) {
    Axios.get(`api/tutorials/${tutorialId}/steps`)
      .then(response => {
        this.setState({ steps: response.data })
      })
      .catch(function (error) {
        console.log(error);
      })
  },

  deleteStep: function (stepId) {
    Axios.delete(`api/steps/${stepId}`)
      .then(response => {
        console.log(response.data)
        this.fetchSteps(this.props.match.params.id);
      })
      .catch(function (error) {
        console.log(error);
      })
  }
}
