import React, { Component } from 'react'
import styled from 'styled-components'
import { satin } from '../../utils/colors';
import { Icon } from '../../utils/icons';

export const StepWrapper = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  padding: 2em;
  border-bottom: 1px solid ${satin};
`;

const LeftSide = styled.div`
  width: 100%;
  min-height: 10em;
  text-align: right;
  background-color: grey;
  padding-right: 5em;
`;

class Step extends Component {
  render() {
    const { _id, step_description, step_media, deleteStep } = this.props
    return (
      <StepWrapper>
        <LeftSide>
          {step_description}
        </LeftSide>
        <img
          src={step_media}
          alt={step_description}
          style={{ 'maxWidth': '60%', 'maxHeight': '100%', 'marginRight': '2em' }}
        />
        <Icon
          className="material-icons"
          onClick={() => deleteStep(_id)}
          title='Delete this step'
        >
          highlight_off
        </Icon>
      </StepWrapper>
    )
  }
}

export default Step;
