import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components';

import { seaGreen, orchid, champagne, satin, darkBlue, blizzardBlue } from '../../utils/colors';

const Wrapper = styled.section`
  text-align: center;
  padding: 4em;
  background: ${satin};
  margin: 2em auto;
  box-shadow: 0 1em 1em rgba(255, 255, 255, 0.1);

  transition: transform 300ms ease;
  ${'' /* transition: box-shadow; */}
  &:hover {
    transform: translate(0px, -0.3em);
    box-shadow: 0 1em 1em rgba(255, 255, 255, 0.2);
  }
`;

const TutorialLink = styled(Link)`
  text-decoration: none;
  color: ${blizzardBlue};
`
const TutorialActions = styled('div')`
  display: flex;
`

class Tutorial extends Component {
  render() {
    const { _id, tutorial_title, deleteTutorial } = this.props;
    return (
      <Wrapper key={_id} >
        <TutorialLink to={`/tutorials/${_id}`}>
          <h1>{tutorial_title}</h1>
        </TutorialLink>
        <div className='tutorial-actions'>
          <TutorialLink to='' onClick={() => deleteTutorial(_id)}>
            Delete this tutorial
        </TutorialLink>
        </div>
      </Wrapper>
    )
  }
}

export default Tutorial;
