import React, { Component } from 'react'
import styled from 'styled-components'
import { satin, satinTransparent, blizzardBlue } from '../../utils/colors';
import { Icon } from '../../utils/icons';

export const StepWrapper = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  padding: 2em;
  border-bottom: 1px solid ${satin};
`;

const LeftSide = styled.div`
  display: flex;
  justify-content: flex-end;
  align-items: center;
  width: 100%;
  max-width: 40%;
  min-height: 10em;
  text-align: right;
  background-color: ${satinTransparent};
  color: ${blizzardBlue};
  padding-right: 5em;
`;

class Step extends Component {
  render() {
    const { _id, step_description, step_media, deleteStep } = this.props
    return (
      <StepWrapper>
        <LeftSide>
          {step_description}
        </LeftSide>
        { step_media != '' ?
        (<img
          src={step_media}
          alt={step_description}
          style={{ 'maxWidth': '60%', 'maxHeight': '100%', 'marginRight': '2em' }}
        />)
        : <p style={{ 'maxWidth': '60%', 'maxHeight': '100%', 'margin': '0 2em' }}>No picture for this step</p>}
        <div>
          <Icon
            className="material-icons"
            onClick={() => deleteStep(_id)}
            title='Delete this step'
          >
            highlight_off
        </Icon>
        </div>
      </StepWrapper>
    )
  }
}

export default Step;
