import React, { Component } from 'react';
import API from '../../utils/API';
import Tutorial from './Tutorial'

import { deleteTutorial } from '../../utils/methods';

class TutorialsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tutorials: []
    }
  }

  componentDidMount() {
    this.fetchTutorials();
  }

  fetchTutorials = () => {
    API.fetchTutorials()
      .then(response => {
        console.log(response.data)
        this.setState({ tutorials: response.data })
      })
      .catch(function (error) {
        console.log(error);
      })
  }

  render() {
    const { tutorials } = this.state;
    return (
      <React.Fragment >
        <h1> My Tutorials </h1>
        {tutorials.map(({ _id, tutorial_title }) => {
          return (
            <Tutorial key={_id} _id={_id} tutorial_title={tutorial_title} deleteTutorial={deleteTutorial} />
          )
        })
        }
      </React.Fragment>
    )
  }
}

export default TutorialsList;
