import styled from "styled-components";
import { purple, orchid, blizzardBlue, tumbleweed } from "../../utils/colors";

export const Input = styled.input`
  padding: 1em 2em;
  margin: 2em;
  width: 20em;
  background: none;
  border: none;
  font-size: 1.5em;
  border-bottom: 1px solid ${purple};
  font-family: 'Roboto', sans-serif;
  font-weight: 700;
  color: ${tumbleweed};
  &:focus {
    color: ${tumbleweed};
    outline: none;
  }
`

export const Form = styled.form`
  display: flex;
  justify-content: center;
`

export const SubmitButton = styled.button`
  height: fit-content;
  background: ${purple};
  border: none;
  color: ${orchid};
  padding: 1em 2em;
`
